const emojiPolyfillImages = {
    'tada' :        'data:image/png;base64,',
    'smile' :       'data:image/png;base64,',
    'house' :       'data:image/png;base64,',
    'money' :       'data:image/png;base64,',
    'rocket' :      'data:image/png;base64,',
    'money-wings' : 'data:image/png;base64,',
    'lupa' :        'data:image/png;base64,',
    'chart':        'data:image/png;base64,',
    'computer' :    'data:image/png;base64,',
    'trex' :        'data:image/png;base64,',
    'satellite' :   'data:image/png;base64,',
    'bolt' :        'data:image/png;base64,',

    // add more here 
} 

const Emoji = ({name, size, c}) => (
    <div style={{display:'inline-block', height:(size || '1.1em'), verticalAlign:'middle', marginLeft:'.2em', marginRight:'.2em'}}>
        {[ ...new Array(c || 1)].map((x,i)=><img key={i} src={emojiPolyfillImages[name]} height='100%' />)}
    </div>
)

export default Emoji
